mapper.select:
select substr(t.CDP_LSSUETIME,'0','4') years,t1.CS_JOBAREA "${@com.limp.framework.core.constant.Constant@CODE_TRANS_PRE}JOBAREA", count(*) num from cis_draw_proc t inner join cis_shipinfo t1 on t.cdp_drawno=t1.CS_DRAWNO where substr(t.CDP_LSSUETIME,'0','4')=#{year}
and t.cdp_orgid like concat(#{orgCode},'%')
group by substr(t.CDP_LSSUETIME,'0','4') ,t1.CS_JOBAREA order by substr(t.CDP_LSSUETIME,'0','4') asc ,t1.CS_JOBAREA asc

SELECT substr(t.CDC_CHECKTIME,0,4) years ,t1.CS_JOBAREA LP_CODE_jobarea,count(*) num from cis_draw_check t inner join cis_shipinfo t1 on t.CDC_DRAWNO=t1.CS_DRAWNO
where T.CDC_ORGCODE = #{orgCode}

    and substr(t.CDC_CHECKTIME,0,4)>=#{startYear} and substr(t.CDC_CHECKTIME,0,4)<=#{endYear}

group by substr(t.CDC_CHECKTIME,0,4)  ,t1.CS_JOBAREA  ORDER BY substr(t.CDC_CHECKTIME,0,4) asc
