mapper.select:
select round(avg(t.csc_score),2) from cis_capacity t inner join (select co_orgcode,SUBSTR(CO_FIRSTTIME,0,4) CO_FIRSTTIME,SUBSTR(max(CO_ENDTIME),0,4) co_endtime from CIS_ORGCERTIFICATE group by co_orgcode,CO_FIRSTTIME) t1 on t.csc_refid = t1.CO_ORGCODE where
#{year}>=SUBSTR(t1.CO_FIRSTTIME,0,4) and t.csc_belongmodel='2001' and t.csc_type='1'
and t1.co_orgcode like #{likeStr}

select count(1) from cis_organinfo t inner join (select co_orgcode,SUBSTR(CO_FIRSTTIME,0,4) CO_FIRSTTIME,SUBSTR(max(CO_ENDTIME),0,4) co_endtime from CIS_ORGCERTIFICATE group by co_orgcode,CO_FIRSTTIME) t1 on t.CO_ORGCODE = t1.CO_ORGCODE
#{year}>=SUBSTR(t1.CO_FIRSTTIME,0,4)
and t1.co_orgcode like #{likeStr}
