mapper.select:
select count(DISTINCT CSC_YCBM) from CIS_SHIP_CHECK t

  substr(t.csc_checktime,0,4) = #{yearLike}

    and t.csc_orgcode like #{likeStr}

SELECT case when t2.nums !=0 then round(((t2.nums-t1.nums)/t2.nums)*100,2) else 0 END FROM
(select count(distinct CSC_YCBM) nums from cis_ship_check where CSC_CHECKPASS =2 AND substr(CSC_CHECKTIME,0,4)=to_number(to_char(sysdate,'yyyy'))-1 AND CSC_YCBM IN
    (select CS_SHIPCODE from CIS_SHIPINFO
    where #{bYearsOld}<=(to_number(to_char(sysdate,'yyyy'))-to_Number(substr( CS_COMPLETDATE,0,4))) and (to_number(to_char(sysdate,'yyyy'))-to_Number(substr( CS_COMPLETDATE,0,4)))<= #{eYearsOld}

      AND CS_ENTBULID = #{pid}

    )

) t1,
(select count(distinct CSC_YCBM) nums from cis_ship_check where substr(CSC_CHECKTIME,0,4)=to_number(to_char(sysdate,'yyyy'))-1 AND CSC_YCBM IN
    (select CS_SHIPCODE from CIS_SHIPINFO
    where #{bYearsOld}<=(to_number(to_char(sysdate,'yyyy'))-to_Number(substr( CS_COMPLETDATE,0,4))) and (to_number(to_char(sysdate,'yyyy'))-to_Number(substr( CS_COMPLETDATE,0,4)))<= #{eYearsOld}

      AND CS_ENTBULID = #{pid}

    )
) t2

select count(distinct csc_ycbm) from CIS_SHIP_CHECK t
where CSC_CHECKTIME like #{year}

  AND CSC_ORGCODE LIKE  #{uid}

SELECT case when t2.nums !=0 then round(((t2.nums-t1.nums)/t2.nums)*100,2) else 0 END FROM
    (select count(distinct CSC_YCBM) nums from cis_ship_check  csc
    inner join cis_shipinfo cs on cs.cs_shipcode = csc.csc_ycbm
    where CSC_CHECKPASS =2  AND CS.CS_JOBAREA=#{workArea}

      AND cs.CS_ORGID LIKE #{uid}


      AND cs.CS_OWNERNO = #{ownerId}


      AND CSC_CHECKTIME like #{year}

    )t1,

    (select count(distinct CSC_YCBM) nums from cis_ship_check  csc
    inner join cis_shipinfo cs on cs.cs_shipcode = csc.csc_ycbm
    where CS.CS_JOBAREA=#{workArea}

      AND cs.CS_ORGID LIKE #{uid}


      AND cs.CS_OWNERNO = #{ownerId}


      AND CSC_CHECKTIME like #{year}

    )t2

SELECT t2.year "year",case when t2.nums !=0 then round(((t2.nums-nvl(t1.nums,0))/t2.nums)*100,2) else 0 END "rate" FROM
  (select substr(CSC_CHECKTIME,0,4) year, count(distinct CSC_YCBM) nums from cis_ship_check  csc
  inner join cis_shipinfo cs on cs.cs_shipcode = csc.csc_ycbm
  where CSC_CHECKPASS =2 AND CS.CS_JOBAREA=#{jobArea}

    AND cs.CS_ORGID LIKE #{likeStr}

  group by substr(CSC_CHECKTIME,0,4))t1
right join

  (select substr(CSC_CHECKTIME,0,4) year,count(distinct CSC_YCBM) nums from cis_ship_check  csc
  inner join cis_shipinfo cs on cs.cs_shipcode = csc.csc_ycbm
  where  CS.CS_JOBAREA=#{jobArea}

    AND cs.CS_ORGID LIKE #{likeStr}

  group by substr(CSC_CHECKTIME,0,4)) t2
on t1.year=t2.year
where t2.year between #{bYear} and #{eYear}
order by t2.year asc

SELECT t2.name "name",case when t2.nums !=0 then round(((t2.nums-nvl(t1.nums,0))/t2.nums)*100,2) else 0 END "rate" FROM (select CSO.CSO_NAME name, count(distinct CSC_YCBM) nums from cis_ship_check csc inner join cis_shipinfo cs on cs.cs_shipcode = csc.csc_ycbm inner join cis_shipowner cso on cso.cso_id = cs.cs_ownerno where cso.cso_type='1' and csc_checkpass='2' AND CSC_CHECKTIME like #{year} AND cs.cs_jobArea =#{jobArea}
AND cs.CS_ORGID LIKE #{likeStr}
group by CSO.CSO_NAME )t1 right join (select CSO.CSO_NAME name, count(distinct CSC_YCBM) nums from cis_ship_check csc inner join cis_shipinfo cs on cs.cs_shipcode = csc.csc_ycbm inner join cis_shipowner cso on cso.cso_id = cs.cs_ownerno where cso.cso_type='1' AND CSC_CHECKTIME like #{year} AND cs.cs_jobArea =#{jobArea}
AND cs.CS_ORGID LIKE #{likeStr}
group by CSO.CSO_NAME ) t2 on t1.name=t2.name order by "rate" DESC

mapper.sql:
from CIS_SHIP_CHECK csc
inner join cis_shipinfo csi on csc.CSC_YCBM = csi.cs_shipcode
where substr(csc.CSC_CHECKTIME ,0,4) >=#{bYear} and  substr(csc.CSC_CHECKTIME ,0,4)  <=#{eYear}

  AND csc.CSC_ORGCODE LIKE #{likeStr}

mapper.select:
select  count(DISTINCT csi.cs_shipcode)
from CIS_SHIP_CHECK csc inner join cis_shipinfo csi on csc.CSC_YCBM = csi.cs_shipcode
where substr(csc.CSC_CHECKTIME ,0,4) >=#{bYear} and  substr(csc.CSC_CHECKTIME ,0,4)  <=#{eYear}

  AND csc.csc_orgcode LIKE #{likeStr}

group by substr(csc.CSC_CHECKTIME ,0,4)
order by substr(csc.CSC_CHECKTIME ,0,4) asc

select "meterial",count(distinct cs_shipcode) "nums"
from cis_ship_check csc inner join cis_shipinfo cs on csc.CSC_YCBM=cs.cs_shipcode
where cs.cs_jobArea =#{jobArea} and csc.CSC_CHECKTYPE ='1_1' and csc.CSC_CHECKTIME like #{year}
and cs.CS_ORGID like #{likeStr}
group by order by asc
